INSERT INTO public.topics VALUES ('948f063a-3c6f-4712-93b8-beb9aa5d5a24', 'c15035cd-5d64-4e3e-99e1-9e3fb75d1bb3', 'Актриса "Властелин колец" и сериала "Остаться в живых" Эванжелин Лилли сообщила о серьёзных последствиях травмы мозга', NULL, NULL, NULL, 'https://www.playground.ru/misc/news/aktrisa_vlastelin_kolets_i_seriala_ostatsya_v_zhivyh_evanzhelin_lilli_soobschila_o_seryoznyh_posledstviyah_travmy_mozga-1814689#utm_source=rss&amp;utm_medium=referral&amp;utm_campaign=news', 'https://i.playground.ru/e/cMmti7bQdBxPq9-FCGHnHw.jpeg', 'Актриса Эванжелин Лилли и звезда "Хоббита", "Остаться в живых" и "Человек-муравей" от Marvel, сообщила, что у неё диагностировали повреждение мозга, полученное в результате падения в мае 2025 года. По её словам, результаты медицинских сканирований показали...', NULL, NULL, '{"Эванжелин Лилли","Властелин колец","Остаться в живых"}', 'pending', 'en', 82, 0, 'new', '2026-01-05 16:18:18', '2026-01-05 22:05:51.137719', 39);
